<?phpnamespace Admin\Controller;use Admin\Controller;class ProductAttrController extends RootController{	/**	 * 编辑单个GoodsAttr	 */	public function edit(){		if(IS_POST){			$res = M('GoodsAttr')->data($_POST)->save();			if($res){				$this->redirect('GoodsCate/edit',array('id' =>$_GET['id'],'on'=>2));			}else{				$this->error('编辑出错！');			}		}else{			$where['id']=$_GET['id'];			$res=M('GoodsAttr')->where($where)->find();			//dump($res);			$this->assign('item',$res);			$this->display();		}	}	public function delete(){		$res = M('GoodsAttr')->where(array('id'=>$_GET['id']))->delete();		if($res){			$this->redirect('GoodsCate/edit',array('id' =>$_GET['cateid'],'on'=>2));		}else{			$this->error('删除出错！');		}	}	}