.dm-uploader.js"></script><!--ueditor--><script type="text/javascript" src="/Public/Plugs/ueditor/ueditor.config.js"></script><script type="text/javascript" src="/Public/Plugs/ueditor/ueditor.all.js"></script><link rel="stylesheet" type="text/css" href="/Public/Plugs/ueditor/third-party/video-js/video-js.css"><script type="text/javascript" src="/Public/Plugs/ueditor/third-party/video-js/video.js"></script><!--app--><link rel="stylesheet" type="text/css" href="/Public/Admin/css/style.css"/><script type="text/javascript" src="/Public/Admin/js/common_admin.js"></script>
    
</head>
<body>

<div class="g-page-in">
    <div class="g_tab">	<div class="title">图片库</div>	<div class="clear"></div></div><div class="main_b" style="border-bottom:#eee 1px solid;">	<table class="g_form">		<tr>			<td width="80" align="right"><b>图片上传：</b></td>			<td>				<div class="mod-piclist-upload">					<div class="left">						<input id="file_upload" name="file_upload" type="file" multiple="true" style="display:inline-block;" />						<input type="hidden" name="thumb" value="" />					</div>					<div class="right">						<span class="tip">格式支持jpg、png、gif，图片大小&lt;50MB</span>					</div>					<div class="clear"></div>				</div>			</td>		</tr>	</table></div><script type="text/javascript">	$(function() {		$('#file_upload').uploadify({			'removeTimeout' : 0,			'fileTypeExts' : '*.gif; *.jpg; *.png; *.jpeg',			'fileSizeLimit' : (1024*50)+'KB',			'formData' : { '<?php echo session_name();?>' : '<?php echo session_id();?>'},			'swf'      : '/Public/Plugs/uploadify/uploadify.swf',			'uploader' : '<?php echo U("Picture/upload");?>',			'buttonImage' : '/Public/Plugs/uploadify/btn.png',			'width':80,			'height':30,			'uploadLimit':999,			'onQueueComplete':function(file,data,response){			//alert(file);				window.location=window.location;			}		});	});</script><div class="mod_piclist_001">	<form name="delete_all" action="<?php echo U('Picture/delete');?>" method="post">		<div class="mod_piclist_001_hander">			<a class="select_all">全选</a>			<a class="delete_all" href="#">批量回收</a>		</div>		<ul>			<?php if(is_array($piclist)): $i = 0; $__LIST__ = $piclist;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><li>					<div class="pic">						<div class="pic_in"							 onclick="return detail(<?php echo ($vo["width"]); ?>,<?php echo ($vo["height"]); ?>,'<?php echo ($vo["path"]); ?>','<?php echo (date('Y-m-d H:i:s',$vo["addtime"])); ?>')">							 <?php if(!empty($vo["path_min"])): ?><img src="<?php echo ($vo["path_min"]); ?>" width="100" />							 <?php else: ?>							 <img src="<?php echo ($vo["path"]); ?>" width="100" /><?php endif; ?>						</div>						<label class="checkbox"><input type="checkbox" name="ids[]" value="<?php echo ($vo["id"]); ?>" /></label>					</div>					<div class="size"><?php echo ($vo["width"]); ?>×<?php echo ($vo["height"]); ?></div>					<div class="info">						<a class="detail"						   onclick="return detail(<?php echo ($vo["width"]); ?>,<?php echo ($vo["height"]); ?>,'<?php echo ($vo["path"]); ?>','<?php echo (date('Y-m-d H:i:s',$vo["addtime"])); ?>')">详情</a>						<a class="delete" onclick="return del(<?php echo ($vo["id"]); ?>)" href="#">删除</a>					</div>				</li><?php endforeach; endif; else: echo "" ;endif; ?>		</ul>	</form></div><?php if(!empty($pages)): ?><div class="h10"></div>	<div class="g_pages"><div class="in"><?php echo ($pages); ?></div></div><?php endif; ?><div class="plug_piclist_win">	<img class="main" src="" />	<div class="info">		<ul>			<li>				<div class="head">图片地址</div>				<div id="pic_path" class="con"></div>			</li>			<li>				<div class="head">上传时间</div>				<div id="pic_time" class="con"></div>			</li>		</ul>		<a class="close">×</a>	</div></div><script>	function detail(w,h,p,t){		w_old = w;		var h_max = $(window).height()>$('html').height()?$(window).height():$('html').height();		if(w>$(window).width()-240){			w = $(window).width()-280;			h = Math.floor(h*w/w_old);		}		if(h+140>h_max){			h_old = h;			h = h_max-160;			w = Math.floor(w*h/h_old);		}		var lft = ($(window).width()-w-240)/2;		//h_max = h_max>h+40?h_max:h+40;		var $lock = lock();		$(".plug_piclist_win").css({display:'block',width:w,height:h,left:lft,top:100+$(window).scrollTop()});		$(".plug_piclist_win").find(".info").css({height:h+20});		$(".plug_piclist_win").find("img.main").css({width:w,height:h}).attr("src",p);		$("#pic_path").text(p);		$("#pic_time").text(t);		function lock(h){			return $("<div class='opacity_2'></div>").css({width:'100%',height:h_max,background:'#000',position:'absolute',left:0,top:0}).appendTo('body');		}		$(".plug_piclist_win .info a.close").click(function(){			$lock.remove();			$(".plug_piclist_win").hide();		});	}	//ajax删除图片	function del($id){		$.confirm({text:"确定删除？"},function(){			$.ajax({				type:"post",				url:"<?php echo U('Picture/ajax_delete_one');?>",				data:{id:$id},				success:function(data){					if(data['status']){						window.location = window.location;					}				}			});		});		return false;	}	//全选图片	$(".mod_piclist_001 .select_all").click(function(){		var now_checked = $(".mod_piclist_001").find(':checked').length,len = $(".mod_piclist_001").find("input[type=checkbox]").length;		if(now_checked == len){			$(".mod_piclist_001").find("input[type=checkbox]").removeAttr("checked");			return false;		}else{			$(".mod_piclist_001").find("input[type=checkbox]").prop({"checked":true});			return false;		}	});	//删除图片	$(".mod_piclist_001").find('.delete_all').click(function(){		$.confirm({text:'确定批量删除？',title:'删除文件'},function(){			$("form[name=delete_all]").submit();		});		return false;	});</script>
</div>

