<body>

<div class="g-page-in">
    <div class="g_tab">	<div class="title">库回收站</div>	<dl>		<dd class="on">图片回收站</dd>		<dd><a href="<?php echo U('File/filelist_rubbish');?>">文件回收站</a></dd>	</dl>	<div class="clear"></div></div><div class="mod_piclist_001">	<form name="delete_all" action="<?php echo U('Picture/absolute_delete');?>" method="post">		<div class="mod_piclist_001_hander">			<a class="select_all" href="#">全选</a>			<a class="delete_all" href="#">清空所选</a>&nbsp;&nbsp;			<a class="clear_all" href="#">清空图片回收站</a>		</div>		<ul>			<?php if(is_array($piclist)): $i = 0; $__LIST__ = $piclist;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><li>					<div class="pic">						<div class="pic_in"							 onclick="return detail(<?php echo ($vo["width"]); ?>,<?php echo ($vo["height"]); ?>,'<?php echo ($vo["path"]); ?>','<?php echo (date('Y-m-d H:i:s',$vo["addtime"])); ?>')">							 							<img src="<?php echo (img_rename($vo["path"],'_min')); ?>" width="100" />						</div>						<label class="checkbox"><input type="checkbox" name="ids[]" value="<?php echo ($vo["id"]); ?>" /></label>					</div>					<div class="size"><?php echo ($vo["width"]); ?>×<?php echo ($vo["height"]); ?></div>					<div class="info">						<a class="detail"						   onclick="return detail(<?php echo ($vo["width"]); ?>,<?php echo ($vo["height"]); ?>,'<?php echo ($vo["path"]); ?>','<?php echo (date('Y-m-d H:i:s',$vo["addtime"])); ?>')">详情</a>						<a class="delete" onclick="return absolute_del(<?php echo ($vo["id"]); ?>)" href="#">清除</a>					</div>				</li><?php endforeach; endif; else: echo "" ;endif; ?>		</ul>	</form></div><?php if(!empty($pages)): ?><div class="h10"></div>	<div class="g_pages"><div class="in"><?php echo ($pages); ?></div></div><?php endif; ?><div class="plug_piclist_win">	<img class="main" src="" />	<div class="info">		<ul>			<li>				<div class="head">图片地址</div>				<div id="pic_path" class="con"></div>			</li>			<li>				<div class="head">上传时间</div>				<div id="pic_time" class="con"></div>			</li>		</ul>		<a class="close">×</a>	</div></div><script>	function detail(w,h,p,t){		w_old = w;		if(w>$(window).width()-240){			w = $(window).width()-280;			h = Math.floor(h*w/w_old);		}		var lft = ($(window).width()-w-240)/2;		var h_max = $(window).height()>$('html').height()?$(window).height():$('html').height();		h_max = h_max>h+40?h_max:h+40;		var $lock = lock();		$(".plug_piclist_win").css({display:'block',width:w,height:h,left:lft,top:100+$(window).scrollTop()});		$(".plug_piclist_win").find(".info").css({height:h+20});		$(".plug_piclist_win").find("img.main").css({width:w,height:h}).attr("src",p);		$("#pic_path").text(p);		$("#pic_time").text(t);		function lock(h){			return $("<div class='opacity_2'></div>").css({width:'100%',height:h_max,background:'#000',position:'absolute',left:0,top:0}).appendTo('body');		}		$(".plug_piclist_win .info a.close").click(function(){			$lock.remove();			$(".plug_piclist_win").hide();		});	}	//ajax清除图片	function absolute_del($id){		$.confirm({text:"确定清除？"},function(){			$.ajax({				type:"post",				url:"<?php echo U('Picture/ajax_absolute_delete_one');?>",				data:{id:$id},				success:function(data){					if(data['status']){						window.location = window.location;					}				}			});		});		return false;	}	//全选图片	$(".mod_piclist_001 .select_all").click(function(){		var now_checked = $(".mod_piclist_001").find(':checked').length,len = $(".mod_piclist_001").find("input[type=checkbox]").length;		if(now_checked == len){			$(".mod_piclist_001").find("input[type=checkbox]").removeAttr("checked");			return false;		}else{			$(".mod_piclist_001").find("input[type=checkbox]").prop({"checked":true});			return false;		}	});	$("#delete_checkbox").click(function(){		if(!confirm("确认清除？")){			return false;		}	});	$(".mod_piclist_001").find('.delete_all').click(function(){		$.confirm({text:'确定清除所选？',title:'清除图片'},function(){			$("form[name=delete_all]").submit();		});		return false;	});	$(".mod_piclist_001").find('.clear_all').click(function(){		$.confirm({text:'确定清空图片回收站？',title:'清空图片回收站'},function(){			$.ajax({				type: "post",				url: "<?php echo U('Picture/ajax_clear');?>",				success: function(data){					if(data['status']){						window.location = window.location;					}				}			});			return false;		});		return false;	});</script>
</div>

